// https://leetcode.com/problems/implement-strstr/
#include <iostream>

using namespace std;

class Solution
{
public:
	int strStr(string haystack, string needle)
	{
		if (needle.length() == 0 || needle.length() > haystack.length())
		{
			return 0;
		}
		int index;
		bool flag = false;
		int j = 0;
		for (int i = 0; haystack[i] != '\0'; i++)
		{
			if (haystack[i] == needle[j])
			{
				// starting from the first element in needle
				if (flag == false)
				{
					index = i;
					flag = true;
					j++;
				}
				else
				{
					// last element in the needle
					j++;
				}
				if (i - index + 1 == needle.length() && flag == true)
				{
					return index;
				}
			}
			else
			{
				if (flag == true)
				{
					i = index;
				}
				j = 0;
				flag = false;
			}
		}
		return -1;
	}
};

int main()
{
	Solution sol;

	string haystack = "mississippi";
	string needle = "issip";
	cout << "haystack.length" << haystack.length() << ", needle.length: " << needle.length() << endl;

	cout << sol.strStr(haystack, needle) << endl;
}

