// https://leetcode.com/problems/implement-strstr/
#include <iostream>

using namespace std;

class Solution
{
public:
	int strStr(string haystack, string needle)
	{
		if (needle.length() == 0)
		{
			return 0;
		}
		int index;
		bool flag = false;
		int j = 0;
		for (int i = 0; haystack[i] != '\0'; i++)
		{
			if (haystack.length() - i < needle.length() && flag == false)
			{
				break;
			}
			if (haystack[i] == needle[j])
			{
				// starting from the first element in needle
				if (flag == false)
				{
					index = i;
					flag = true;
					j++;
				}
				else
				{
					// last element in the needle
					j++;
				}
				if (i - index + 1 == needle.length() && flag == true)
				{
					return index;
				}
			}
			else
			{
				if (flag == true)
				{
					i = index;
				}
				j = 0;
				flag = false;
			}
		}
		return -1;
	}

	int strStr2(string haystack, string needle)
	{
		int n = haystack.length();
		int m = needle.length();
		if (m == 0)
			return 0;
		int j;
		for (int i = 0; i < n; i++)
		{
			j = 0;
			if (n - i < m)
			{
				break;
			}
			if (haystack[i] == needle[j])
			{
				while (j < m)
				{
					if (haystack[i + j] == needle[j])
					{
						j++;
					}
					else
					{
						break;
					}
				}
				if (j == m)
				{
					return i;
				}
			}
		}
		return -1;
	}
};

int main()
{
	Solution sol;

	string haystack = "";
	string needle = "";
	cout << "haystack.length" << haystack.length() << ", needle.length: " << needle.length() << endl;

	string hay2 = "aaa";
	string needle2 = "aaa";
	cout << sol.strStr2(hay2, needle2) << endl;
}
